import React, { useState } from "react";
import AddIcon from "@mui/icons-material/Add";
import Fab from "@mui/material/Fab";
import Zoom from "@mui/material/Zoom";


function CreateArea(props){

     const [note, setNote] = useState ({title:"", author:"", summary:""})
     const [clicked, setClick] = useState(false);

     function handleChange(event){
          const{name, value} = event.target
          setNote((prevNote)=>{
               return{
                    ...prevNote,
                    [name]: value
               }
          })
     }

     function submitNote(event){
          props.onAdd(note);
          setNote({
               title: "",
               author:"",
               summary: ""
             });
             event.preventDefault();

     }

     function handleClick() {
          setClick(true);
        }

    
    return <div>
   <form className="create-note">
        <input type="text" name="title" placeholder="Add title" value = {note.title} onChange={handleChange}></input>
        <input type="text" name="author" placeholder="Add author" value={note.author} onChange={handleChange}></input>
        <textarea name="summary" placeholder="Write a summary" value={note.summary} onChange={handleChange}  onClick={handleClick}></textarea>
        <Zoom>
          <Fab onClick={submitNote}>
            <AddIcon />
          </Fab>
        </Zoom>
   </form>
   </div>
}

export default CreateArea
