import React, { useState } from "react";
import AddIcon from "@material-ui/icons/Add";
import Fab from "@material-ui/core/Fab";
import Zoom from "@material-ui/core/Zoom";


function CreateArea(props){

     const [note, setNote] = useState ({title:"", author:"", summary:""})

     function handleChange(event){
          const{name, value} = event.target
          setNote((prevNote)=>{
               return{
                    ...prevNote,
                    [name]: value
               }
          })

     }

    
    return <div>
   <form className="create-note">
        <input type="text" name="title" placeholder="Add title" value = {note.title} onChange={handleChange}></input>
        <input type="text" name="author" placeholder="Add author" value={note.author} onChange={handleChange}></input>
        <textarea name="summary" placeholder="Write a summary" value={note.summary} onChange={handleChange}></textarea>
        <Zoom in={clicked ? true : false}>
          <Fab onClick={submitNote}>
            <AddIcon />
          </Fab>
        </Zoom>
   </form>
   </div>
}

export default CreateArea
